import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import { useAuth } from "../context/AuthContext"; // Corrected usage
import "../styles/Shifts.css";

const Shifts = () => {
  const { user } = useAuth(); // Removed passing AuthProvider here
  const [shifts, setShifts] = useState([]);

  useEffect(() => {
    // Sample static data, you can fetch from API instead
    const sampleShifts = [
      {
        id: 1,
        title: "Morning Shift - A",
        start: "2025-04-05T06:00:00",
        end: "2025-04-05T14:00:00",
      },
      {
        id: 2,
        title: "Afternoon Shift - B",
        start: "2025-04-05T14:00:00",
        end: "2025-04-05T22:00:00",
      },
      {
        id: 3,
        title: "Evening Shift - C",
        start: "2025-04-05T22:00:00",
        end: "2025-04-06T06:00:00", // Corrected next-day shift
      },
    ];
    setShifts(sampleShifts);
  }, []);

  return (
    <div className="shifts-wrapper">
      <h2 className="text-xl font-semibold mb-4">Shift Schedule</h2>
      <div className="shifts-calendar">
        <FullCalendar
          plugins={[interactionPlugin, dayGridPlugin, timeGridPlugin]}
          initialView="timeGridDay"
          allDaySlot={false}
          slotMinTime={"06:00:00"}
          slotMaxTime={"24:00:00"} // Max time supported by FullCalendar
          headerToolbar={{
            left: "prev,next today",
            center: "title",
            right: "timeGridDay,timeGridWeek,dayGridMonth",
          }}
          events={shifts}
          editable={false}
          height="auto"
        />
      </div>
    </div>
  );
};

export default Shifts;
