import { Outlet, Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { useEffect } from "react";
import "./Layout.css";

function Layout() {
  const { isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/", { replace: true }); // ðŸ”¥ Redirect and prevent back navigation
  };

  // ðŸ”¥ Prevent access to protected pages after logout (back button fix)
  useEffect(() => {
    if (!isAuthenticated) {
      navigate("/", { replace: true });
    }
  }, [isAuthenticated, navigate]);

  return (
    <div className="container">
      <header className="navbar">
        <h1 className="logo">SOC Portal</h1>
        <nav>
          <ul className="nav-links">
            {!isAuthenticated ? (
              <>
                <li><Link to="/register">Register</Link></li>
                <li><Link to="/login">Login</Link></li>
              </>
            ) : (
              <>
                <li><Link to="/dashboard">Dashboard</Link></li>
                <li><Link to="/shifts">Shifts</Link></li>
                <li><button onClick={handleLogout} className="logout-btn">Logout</button></li>
              </>
            )}
          </ul>
        </nav>
      </header>
      
      <main className="content">
        <Outlet />
      </main>
      
      <footer className="footer">
        <p>Â© 2025 Grant Thornton Bharat LLP - All Rights Reserved.</p>
      </footer>
    </div>
  );
}

export default Layout;
