import { Outlet, Link, useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { useEffect, useState } from "react";
import "../styles/Layout.css";

const protectedRoutes = [
  "/dashboard",
  "/roster",
  "/shift_handover",
  "/knowledge_base",
  "/operation_runbook",
  "/advisory_system"
];

function Layout() {
  const { isAuthenticated, logout, login } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [navbarColor, setNavbarColor] = useState("#121212");

  const handleLogin = () => {
    login();
    navigate("/login");
  };

  const handleRegister = () => {
    navigate("/register");
  };

  const handleLogout = () => {
    logout();
    navigate("/", { replace: true });
  };

  useEffect(() => {
    if (!isAuthenticated && protectedRoutes.includes(location.pathname)) {
      navigate("/", { replace: true });
    }
  }, [isAuthenticated, location.pathname, navigate]);

  useEffect(() => {
    if (isAuthenticated && location.pathname === "/") {
      navigate("/dashboard", { replace: true });
    }
  }, [isAuthenticated, location.pathname, navigate]);

  return (
    <>
      {/* Liquid Glass Navbar */}
      <div className="navbar">
        <div className="logo-container">
          <img src="/logo.png" className="logo-image" alt="Logo" />
          <h1 className="logo-text">Grant Thornton</h1>
        </div>

        {!isAuthenticated ? (
          <>
            <Link to="/register">Register</Link>
            <Link to="/login">Login</Link>
          </>
        ) : (
          <>
            <Link to="/dashboard">Dashboard</Link>
            <Link to="/roster">Roster</Link>
            <Link to="/shift_handover">Shift Handover</Link>
            <Link to="/knowledge_base">Knowledge Base</Link>
            <Link to="/operation_runbook">Operation Runbook</Link>
            <Link to="/advisory_system">Advisory System</Link>
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </>
        )}
      </div>

      {/* Main Content */}
      <div className="content">
        <Outlet />
      </div>

      {/* Footer */}
      <footer className="footer">
        Â© 2025 Grant Thornton Bharat LLP - All Rights Reserved.
      </footer>
    </>
  );
}

export default Layout;
