import React, { useState, useEffect, useContext } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import { AuthProvider, useAuth } from "../context/AuthContext";
import "../styles/Shifts.css";

const Shifts = () => {
  const { user } = useAuth(AuthProvider); // Get logged-in user
  const [shifts, setShifts] = useState([]);

  // Dummy shift data (will be fetched from backend later)
  useEffect(() => {
    const sampleShifts = [
      { id: 1, title: "Morning Shift - A", start: "2025-04-05T08:00:00", end: "2025-04-05T14:00:00" },
      { id: 2, title: "Afternoon Shift - B", start: "2025-04-05T14:00:00", end: "2025-04-05T20:00:00" },
      { id: 3, title: "Evening Shift - C", start: "2025-04-05T20:00:00", end: "2025-04-06T02:00:00" },
    ];
    setShifts(sampleShifts);
  }, []);

  return (
    <div className="shifts-container">
      <FullCalendar
        plugins={[interactionPlugin, dayGridPlugin, timeGridPlugin]}
        initialView="timeGridDay"
        slotMinTime={"08:00:00"}
        slotMaxTime={"22:00:00"}
        headerToolbar={{
          left: "prev,next today",
          center: "title",
          right: "timeGridDay,timeGridWeek,dayGridMonth",
        }}
        events={shifts}
        editable={false} // Users cannot drag events
      />
    </div>
  );
};

export default Shifts;
