import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import { useAuth } from "../context/AuthContext";
import "../styles/Shifts.css";

Modal.setAppElement("#root");

const shiftTypes = ["Morning", "Evening", "Night"];

const getCurrentWeek = () => {
  const today = new Date();
  const start = today.getDate() - today.getDay(); // Sunday
  return [...Array(7)].map((_, i) => {
    const date = new Date(today.setDate(start + i));
    return date.toISOString().slice(0, 10);
  });
};

const Shifts = () => {
  const { user } = useAuth();
  const isAdmin = user?.role === "admin";

  const [shifts, setShifts] = useState([]);
  const [analysts, setAnalysts] = useState([]);
  const [weekDates, setWeekDates] = useState(getCurrentWeek());

  const [selectedShift, setSelectedShift] = useState(null);
  const [comment, setComment] = useState("");
  const [commentsMap, setCommentsMap] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);

  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [newShiftDate, setNewShiftDate] = useState("");
  const [newShiftType, setNewShiftType] = useState("Morning");
  const [selectedEmployees, setSelectedEmployees] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.49:5000/api/shifts")
      .then((res) => res.json())
      .then((data) => setShifts(data))
      .catch((err) => console.error("Failed to fetch shifts:", err));

    fetch("http://192.168.1.49:5000/api/analysts")
      .then((res) => res.json())
      .then((data) => setAnalysts(data))
      .catch((err) => console.error("Failed to fetch analysts:", err));
  }, []);

  const openShiftModal = (shift) => {
    setSelectedShift(shift);
    setComment(commentsMap[shift.id] || "");
    setIsModalOpen(true);
  };

  const handleSave = () => {
    if (selectedShift) {
      setCommentsMap((prev) => ({
        ...prev,
        [selectedShift.id]: comment,
      }));
    }
    setIsModalOpen(false);
  };

  const handleEmployeeToggle = (username) => {
    setSelectedEmployees((prev) =>
      prev.includes(username)
        ? prev.filter((u) => u !== username)
        : [...prev, username]
    );
  };

  const handleAddShift = async () => {
    if (selectedEmployees.length === 0 || !newShiftDate || !newShiftType) {
      alert("Please fill all fields.");
      return;
    }

    const usernames = selectedEmployees.join(", ");

    const isDuplicate = shifts.some(
      (shift) =>
        shift.date === newShiftDate &&
        shift.shift_type === newShiftType.toLowerCase() &&
        shift.title === `${usernames} - ${newShiftType}`
    );

    if (isDuplicate) {
      alert("This shift already exists with selected employees.");
      return;
    }

    try {
      const employeeIds = analysts
        .filter((emp) => selectedEmployees.includes(emp.username))
        .map((emp) => emp.id);

      const response = await fetch("http://192.168.1.49:5000/api/create_shift", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          date: newShiftDate,
          shift_type: newShiftType.toLowerCase(),
          employee_ids: employeeIds,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        alert("Error: " + data.error);
        return;
      }

      const newShift = {
        id: data.shift_id,
        title: `${usernames} - ${newShiftType}`,
        date: newShiftDate,
        shift_type: newShiftType.toLowerCase(),
      };

      setShifts((prev) => [...prev, newShift]);
      setIsAddModalOpen(false);
      setSelectedEmployees([]);
      alert("Shift created successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to create shift.");
    }
  };

  const getShiftsByDateAndType = (date, type) => {
    return shifts.filter(
      (s) =>
        s.date === date &&
        s.shift_type.toLowerCase() === type.toLowerCase()
    );
  };

  return (
    <div className="shifts-wrapper">
      {isAdmin && (
        <button onClick={() => setIsAddModalOpen(true)} style={{ marginBottom: "10px" }}>
          Add Shift
        </button>
      )}

      <div className="custom-shift-grid">
        <div className="grid-header">
          <div className="cell shift-label-header">Shift</div>
          {weekDates.map((date) => (
            <div className="cell" key={date}>
              {new Date(date).toLocaleDateString("en-US", { weekday: "short", month: "short", day: "numeric" })}
            </div>
          ))}
        </div>

        {shiftTypes.map((type) => (
          <div className="grid-row" key={type}>
            <div className="cell shift-label">{type}</div>
            {weekDates.map((date) => {
              const shiftsHere = getShiftsByDateAndType(date, type);
              return (
                <div className="cell" key={`${date}-${type}`}>
                  {shiftsHere.length > 0 ? (
                    shiftsHere.map((shift) => (
                      <div
                        key={shift.id}
                        className="shift-box"
                        title={shift.title}
                        onClick={() => openShiftModal(shift)}
                      >
                        {shift.title}
                      </div>
                    ))
                  ) : (
                    <span style={{ color: "#aaa" }}>â€”</span>
                  )}
                </div>
              );
            })}
          </div>
        ))}
      </div>

      {/* Modal for Comments (Can be extended to Edit/Delete) */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={() => setIsModalOpen(false)}
        className="modal"
        overlayClassName="overlay"
      >
        <h2>Shift Details</h2>
        <textarea
          rows={10}
          cols={60}
          value={comment}
          onChange={(e) => setComment(e.target.value)}
        />
        <div style={{ marginTop: "10px" }}>
          <button onClick={handleSave} style={{ marginRight: "10px" }}>
            Save
          </button>
          <button onClick={() => setIsModalOpen(false)}>Close</button>
        </div>
      </Modal>

      {/* Modal for Adding Shift */}
      <Modal
        isOpen={isAddModalOpen}
        onRequestClose={() => setIsAddModalOpen(false)}
        className="modal"
        overlayClassName="overlay"
      >
        <h2>Create Shift</h2>

        <label>Select Employees:</label>
        <div style={{ border: "1px solid #ccc", padding: "10px", maxHeight: "150px", overflowY: "scroll" }}>
          {analysts.map((emp) => (
            <div key={emp.id}>
              <input
                type="checkbox"
                checked={selectedEmployees.includes(emp.username)}
                onChange={() => handleEmployeeToggle(emp.username)}
              />
              <label style={{ marginLeft: "8px" }}>{emp.username}</label>
            </div>
          ))}
        </div>

        <label>Shift Type:</label>
        <select value={newShiftType} onChange={(e) => setNewShiftType(e.target.value)}>
          {shiftTypes.map((type) => (
            <option key={type}>{type}</option>
          ))}
        </select>

        <label>Date:</label>
        <input type="date" value={newShiftDate} onChange={(e) => setNewShiftDate(e.target.value)} />

        <div style={{ marginTop: "10px" }}>
          <button onClick={handleAddShift} style={{ marginRight: "10px" }}>
            Add Shift
          </button>
          <button onClick={() => setIsAddModalOpen(false)}>Cancel</button>
        </div>
      </Modal>
    </div>
  );
};

export default Shifts;
