import React, { useState } from "react";
import "../styles/ShiftHandover.css";

function ShiftHandover() {
    const [handoverData, setHandoverData] = useState({
        date: "06 July 2025",
        shift: "Afternoon Shift (2:30 PM to 09:30 PM)",
        cluster: "Cluster 02",
        members: "Anant, Jachandeep, Vikas",
        actionPoints: [
            // ... (existing action points data)
        ],
        currentShiftPoints: [
            // ... (existing current shift points data)
        ],
        lastIncidents: [
            {
                client: "QRadar",
                timings: ["1019372 (POC)", "283869 (Pred One)", "826552 (PROD)"]
            },
            {
                client: "SentinelOne",
                timings: ["", "", ""]
            },
            {
                client: "Seceon",
                subClients: ["Freecharge", "RMSI"],
                severities: [
                    { type: "Major", values: ["-", "07/06/2025, 01:59:06 PM"] },
                    { type: "Minor", values: ["-", "7/6/2025, 07:08:03 PM"] },
                    { type: "System", values: ["-", "-"] },
                    { type: "Critical", values: ["-", "07/06/2025, 04:22:03 PM"] }
                ]
            },
            {
                client: "DLP (Force Point)",
                value: {
                    name: "Orient Electric",
                    timestamp: "2025-07-06 19:45:28"
                }
            }
        ]
    });

    // ... (existing handler functions)

    const handleIncidentEdit = (index, field, value, subIndex = null) => {
        const updatedIncidents = [...handoverData.lastIncidents];
        
        if (subIndex !== null) {
            updatedIncidents[index].timings[subIndex] = value;
        } else if (field === "client") {
            updatedIncidents[index].client = value;
        } else if (field === "subClient") {
            updatedIncidents[index].subClients[subIndex] = value;
        } else if (field === "severity") {
            updatedIncidents[index].severities[subIndex].values = value.split(",");
        } else if (field === "dlpName") {
            updatedIncidents[index].value.name = value;
        } else if (field === "dlpTimestamp") {
            updatedIncidents[index].value.timestamp = value;
        }
        
        setHandoverData({ ...handoverData, lastIncidents: updatedIncidents });
    };

    return (
        <div className="handover_wrapper">
            {/* ... (existing code) */}

            {/* Last Incident Raised Table */}
            <table className="handover_table">
                <thead>
                    <tr>
                        <th colSpan="4" className="table_title">Last Incident Raised</th>
                    </tr>
                    <tr>
                        <th><strong>Client</strong></th>
                        <th colSpan="3"><strong>Timings</strong></th>
                    </tr>
                </thead>
                <tbody>
                    {/* QRadar Row */}
                    <tr>
                        <td>
                            <strong
                                contentEditable
                                suppressContentEditableWarning
                                onBlur={(e) => handleIncidentEdit(0, "client", e.target.innerText)}
                            >
                                {handoverData.lastIncidents[0].client}
                            </strong>
                        </td>
                        {handoverData.lastIncidents[0].timings.map((timing, i) => (
                            <td
                                key={`qradar-${i}`}
                                contentEditable
                                suppressContentEditableWarning
                                onBlur={(e) => handleIncidentEdit(0, "timing", e.target.innerText, i)}
                            >
                                {timing}
                            </td>
                        ))}
                    </tr>

                    {/* SentinelOne Row */}
                    <tr>
                        <td>
                            <strong
                                contentEditable
                                suppressContentEditableWarning
                                onBlur={(e) => handleIncidentEdit(1, "client", e.target.innerText)}
                            >
                                {handoverData.lastIncidents[1].client}
                            </strong>
                        </td>
                        {handoverData.lastIncidents[1].timings.map((timing, i) => (
                            <td
                                key={`sentinel-${i}`}
                                contentEditable
                                suppressContentEditableWarning
                                onBlur={(e) => handleIncidentEdit(1, "timing", e.target.innerText, i)}
                            >
                                {timing}
                            </td>
                        ))}
                    </tr>

                    {/* Seceon Header Row */}
                    <tr>
                        <td>
                            <strong
                                contentEditable
                                suppressContentEditableWarning
                                onBlur={(e) => handleIncidentEdit(2, "client", e.target.innerText)}
                            >
                                {handoverData.lastIncidents[2].client}
                            </strong>
                        </td>
                        <td>
                            <strong
                                contentEditable
                                suppressContentEditableWarning
                                onBlur={(e) => handleIncidentEdit(2, "subClient", e.target.innerText, 0)}
                            >
                                {handoverData.lastIncidents[2].subClients[0]}
                            </strong>
                        </td>
                        <td colSpan="2">
                            <strong
                                contentEditable
                                suppressContentEditableWarning
                                onBlur={(e) => handleIncidentEdit(2, "subClient", e.target.innerText, 1)}
                            >
                                {handoverData.lastIncidents[2].subClients[1]}
                            </strong>
                        </td>
                    </tr>

                    {/* Seceon Severity Rows */}
                    {handoverData.lastIncidents[2].severities.map((severity, i) => (
                        <tr key={`severity-${i}`}>
                            <td>
                                <strong
                                    contentEditable
                                    suppressContentEditableWarning
                                    onBlur={(e) => handleIncidentEdit(2, "severityType", e.target.innerText, i)}
                                >
                                    {severity.type}
                                </strong>
                            </td>
                            <td
                                contentEditable
                                suppressContentEditableWarning
                                onBlur={(e) => handleIncidentEdit(2, "severityValue", e.target.innerText, i)}
                            >
                                {severity.values[0]}
                            </td>
                            <td colSpan="2"
                                contentEditable
                                suppressContentEditableWarning
                                onBlur={(e) => handleIncidentEdit(2, "severityValue", e.target.innerText, i)}
                            >
                                {severity.values[1]}
                            </td>
                        </tr>
                    ))}

                    {/* DLP Row */}
                    <tr>
                        <td>
                            <strong
                                contentEditable
                                suppressContentEditableWarning
                                onBlur={(e) => handleIncidentEdit(3, "client", e.target.innerText)}
                            >
                                {handoverData.lastIncidents[3].client}
                            </strong>
                        </td>
                        <td colSpan="3" style={{ textAlign: "center" }}>
                            <strong
                                contentEditable
                                suppressContentEditableWarning
                                onBlur={(e) => handleIncidentEdit(3, "dlpName", e.target.innerText)}
                            >
                                {handoverData.lastIncidents[3].value.name}
                            </strong>
                            <br />
                            <span
                                contentEditable
                                suppressContentEditableWarning
                                onBlur={(e) => handleIncidentEdit(3, "dlpTimestamp", e.target.innerText)}
                            >
                                {handoverData.lastIncidents[3].value.timestamp}
                            </span>
                        </td>
                    </tr>
                </tbody>
            </table>
        </div>
    );
}

export default ShiftHandover;
