import { Outlet, Link, useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { useEffect } from "react";
import "../styles/Layout.css";

function Layout() {
  const { isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    logout();
    navigate("/", { replace: true }); // Redirect and prevent back navigation
  };

  // Protected routes (User should stay inside these after login)
  const protectedRoutes = ["/dashboard", "/shifts"];

  // If a logged-out user is in a protected route, send them to "/"
  useEffect(() => {
    if (!isAuthenticated && protectedRoutes.includes(location.pathname)) {
      navigate("/", { replace: true });
    }
  }, [isAuthenticated, location.pathname, navigate]);

  // If a logged-in user is on the landing page ("/"), redirect to Dashboard
  useEffect(() => {
    if (isAuthenticated && location.pathname === "/") {
      navigate("/dashboard", { replace: true });
    }
  }, [isAuthenticated, location.pathname, navigate]);

  return (
    <div className="container">
      <header className="navbar">
        <h1 className="logo">SOC Portal</h1>
        <nav>
          <ul className="nav-links">
            {!isAuthenticated ? (
              <>
                <li><Link to="/register">Register</Link></li>
                <li><button onClick={to} className="logout-btn">Logout</button></li>
              </>
            ) : (
              <>
                <li><Link to="/dashboard">Dashboard</Link></li>
                <li><Link to="/shifts">Shifts</Link></li>
                <li><button onClick={handleLogout} className="logout-btn">Logout</button></li>
              </>
            )}
          </ul>
        </nav>
      </header>
      
      <main className="content">
        <Outlet />
      </main>
      
      <footer className="footer">
        <p>Â© 2025 Grant Thornton Bharat LLP - All Rights Reserved.</p>
      </footer>
    </div>
  );
}

export default Layout;
