import { createContext, useContext, useState, useEffect } from "react";

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    checkSession(); // Check session on component mount
  }, []);

  const login = async (username, password) => {
    try {
      const response = await fetch("http://192.168.29.194:5000/api/login", {
        method: "POST",
        credentials: "include",  // Sends session cookie with request
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (response.ok) {
        setIsAuthenticated(true);  // Successfully authenticated
        return true;
      } else {
        console.error("Login failed:", data.error);
        alert(data.error || "Login failed. Please try again.");
        return false;
      }
    } catch (error) {
      console.error("Error logging in:", error);
      alert("An error occurred while logging in. Please try again later.");
      return false;
    }
  };

  const logout = async () => {
    try {
      const response = await fetch("http://192.168.29.194:5000/api/logout", {
        method: "POST",
        credentials: "include",  // Ensures session cookies are sent
      });
      const data = await response.json();
      
      if (response.ok) {
        setIsAuthenticated(false);  // Log out successfully
      } else {
        console.error("Logout failed:", data.error);
      }
    } catch (error) {
      console.error("Logout error:", error);
      alert("An error occurred while logging out. Please try again later.");
    }
  };

  const checkSession = async () => {
    try {
      const response = await fetch("http://192.168.29.194:5000/api/check_session", {
        method: "GET",
        credentials: "include",  // Sends session cookie with request
      });

      const data = await response.json();
      setIsAuthenticated(data.logged_in);
    } catch (error) {
      console.error("Session check error:", error);
    }
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}
