import React, { useEffect, useRef } from "react";
import "../styles/HomePage.css";

function HomePage() {
  const canvasRef = useRef(null);

  useEffect(() => {
    const App = {
      canvas: null,
      ctx: null,
      width: 0,
      height: 0,
      particles: [],
      deathCount: 0,
      stepCount: 0,
      drawnInLastFrame: 0,
      animationFrame: null,

      setup: function() {
        const canvas = canvasRef.current;
        const container = canvas.parentElement;
        
        this.width = container.clientWidth;
        this.height = container.clientHeight;
        canvas.width = this.width;
        canvas.height = this.height;
        
        this.canvas = canvas;
        this.ctx = canvas.getContext("2d");
        this.dataToImageRatio = 1;
        this.ctx.imageSmoothingEnabled = false;
        this.xC = this.width / 2;
        this.yC = this.height / 2;
        
        this.lifespan = 1000;
        this.popPerBirth = 1;
        this.maxPop = 150;
        this.birthFreq = 2;
        this.gridSize = 20;
        this.gridSteps = Math.floor(1000 / this.gridSize);
        this.grid = [];
        
        let i = 0;
        const gridRadius = Math.min(this.width, this.height) * 0.4;
        for (let xx = -gridRadius; xx < gridRadius; xx += this.gridSize) {
          for (let yy = -gridRadius; yy < gridRadius; yy += this.gridSize) {
            const r = Math.sqrt(xx*xx + yy*yy);
            const r0 = gridRadius * 0.4;
            let field;
            
            if (r < r0) field = 255 / r0 * r;
            else field = 255 - Math.min(255, (r - r0)/2);
            
            this.grid.push({
              x: xx,
              y: yy,
              busyAge: 0,
              spotIndex: i,
              isEdge: (xx === -gridRadius || xx >= (gridRadius - this.gridSize) || 
                      yy === -gridRadius || yy >= (gridRadius - this.gridSize)),
              field: field
            });
            i++;
          }
        }
        this.gridMaxIndex = i;
        
        this.initDraw();
        
        const animate = () => {
          this.evolve();
          this.animationFrame = requestAnimationFrame(animate);
        };
        animate();
        
        this.handleResize = this.handleResize.bind(this);
        window.addEventListener('resize', this.handleResize);
      },

      handleResize: function() {
        const container = canvasRef.current.parentElement;
        this.width = container.clientWidth;
        this.height = container.clientHeight;
        canvasRef.current.width = this.width;
        canvasRef.current.height = this.height;
        this.xC = this.width / 2;
        this.yC = this.height / 2;
      },

      evolve: function() {
        this.stepCount++;
        
        this.grid.forEach(function(e) {
          if (e.busyAge > 0) e.busyAge++;
        });
        
        if (this.stepCount % this.birthFreq === 0 && 
            (this.particles.length + this.popPerBirth) < this.maxPop) {
          this.birth();
        }
        this.move();
        this.draw();
      },

      birth: function() {
        const gridSpotIndex = Math.floor(Math.random() * this.gridMaxIndex);
        const gridSpot = this.grid[gridSpotIndex];
        
        const particle = {
          hue: 200 + Math.floor(50*Math.random()),
          sat: 95,
          lum: 20 + Math.floor(40*Math.random()),
          x: gridSpot.x, 
          y: gridSpot.y,
          xLast: gridSpot.x, 
          yLast: gridSpot.y,
          xSpeed: 0, 
          ySpeed: 0,
          age: 0,
          ageSinceStuck: 0,
          attractor: {
            oldIndex: gridSpotIndex,
            gridSpotIndex: gridSpotIndex,
          },
          name: 'seed-' + Math.ceil(10000000 * Math.random())
        };
        this.particles.push(particle);
      },

      kill: function(particleName) {
        this.particles = this.particles.filter(seed => seed.name !== particleName);
        this.deathCount++;
      },

      move: function() {
        for (let i = 0; i < this.particles.length; i++) {
          const p = this.particles[i];
          p.xLast = p.x; 
          p.yLast = p.y;
          
          let index = p.attractor.gridSpotIndex;
          let gridSpot = this.grid[index];
          
          if (Math.random() < 0.5) {
            if (!gridSpot.isEdge) {
              const neighbors = [
                index - 1, // top
                index + 1, // bottom
                index - this.gridSteps, // left
                index + this.gridSteps  // right
              ];
              
              // Filter valid neighbors and map to grid spots
              const validSpots = neighbors
                .filter(idx => idx >= 0 && idx < this.grid.length)
                .map(idx => this.grid[idx]);
              
              if (validSpots.length > 0) {
                const chaos = 30;
                const maxFieldSpot = validSpots.reduce((max, spot) => 
                  (spot.field + chaos * Math.random()) > 
                  (max.field + chaos * Math.random()) ? spot : max
                );
                
                if (maxFieldSpot.busyAge === 0 || maxFieldSpot.busyAge > 15) {
                  p.ageSinceStuck = 0;
                  p.attractor.oldIndex = index;
                  p.attractor.gridSpotIndex = maxFieldSpot.spotIndex;
                  gridSpot = maxFieldSpot;
                  gridSpot.busyAge = 1;
                } else {
                  p.ageSinceStuck++;
                }
              } else {
                p.ageSinceStuck++;
              }
              
              if (p.ageSinceStuck === 10) this.kill(p.name);
            } else {
              p.ageSinceStuck++;
            }
          }
          
          const k = 8, visc = 0.4;
          const dx = p.x - gridSpot.x;
          const dy = p.y - gridSpot.y;
          
          const xAcc = -k * dx;
          const yAcc = -k * dy;
          
          p.xSpeed += xAcc; 
          p.ySpeed += yAcc;
          p.xSpeed *= visc; 
          p.ySpeed *= visc;
          
          p.x += 0.1 * p.xSpeed; 
          p.y += 0.1 * p.ySpeed;
          p.age++;
          
          if (p.age > this.lifespan) {
            this.kill(p.name);
          }
        }
      },

      initDraw: function() {
        this.ctx.beginPath();
        this.ctx.rect(0, 0, this.width, this.height);
        this.ctx.fillStyle = 'black';
        this.ctx.fill();
        this.ctx.closePath();
      },

      draw: function() {
        this.drawnInLastFrame = 0;
        if (!this.particles.length) return false;
        
        this.ctx.beginPath();
        this.ctx.rect(0, 0, this.width, this.height);
        this.ctx.fillStyle = 'rgba(0, 0, 0, 0.1)';
        this.ctx.fill();
        this.ctx.closePath();
        
        for (let i = 0; i < this.particles.length; i++) {
          const p = this.particles[i];
          const h = p.hue + this.stepCount/30;
          const s = p.sat;
          const l = p.lum;
          
          const last = this.dataXYtoCanvasXY(p.xLast, p.yLast);
          const now = this.dataXYtoCanvasXY(p.x, p.y);
          
          this.ctx.beginPath();
          this.ctx.strokeStyle = `hsla(${h}, ${s}%, ${l}%, 1)`;
          this.ctx.moveTo(last.x, last.y);
          this.ctx.lineTo(now.x, now.y);
          this.ctx.lineWidth = 1.5 * this.dataToImageRatio;
          this.ctx.stroke();
          this.ctx.closePath();
          
          this.drawnInLastFrame++;
        }
      },

      dataXYtoCanvasXY: function(x, y) {
        const zoom = 1.6;
        const xx = this.xC + x * zoom * this.dataToImageRatio;
        const yy = this.yC + y * zoom * this.dataToImageRatio;
        return {x: xx, y: yy};
      }
    };

    App.setup();

    return () => {
      window.removeEventListener('resize', App.handleResize);
      if (App.animationFrame) {
        cancelAnimationFrame(App.animationFrame);
      }
    };
  }, []);

  return (
    <div className="home-container">
      <div className="effect-area">
        <canvas ref={canvasRef} className="particle-canvas" />
      </div>
    </div>
  );
}

export default HomePage;
