import { Outlet, Link, useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { useEffect } from "react";
import "./Layout.css";

function Layout() {
  const { isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    logout();
    navigate("/", { replace: true }); // Redirect and prevent back navigation
  };

  // Centralized navigation logic
  useEffect(() => {
    if (!isAuthenticated && ["/dashboard", "/shifts"].includes(location.pathname)) {
      navigate("/", { replace: true });
    } else if (isAuthenticated && location.pathname === "/") {
      navigate("/dashboard", { replace: true });
    }
  }, [isAuthenticated, location.pathname, navigate]);

  // Render navigation links
  const renderNavLinks = () => {
    if (!isAuthenticated) {
      return (
        <>
          <li><Link to="/register">Register</Link></li>
          <li><Link to="/login">Login</Link></li>
        </>
      );
    }
    return (
      <>
        <li><Link to="/dashboard">Dashboard</Link></li>
        <li><Link to="/shifts">Shifts</Link></li>
        <li>
          <button onClick={handleLogout} className="logout-btn" aria-label="Logout">
            Logout
          </button>
        </li>
      </>
    );
  };

  return (
    <div className="container">
      <header className="navbar">
        <h1 className="logo">SOC Portal</h1>
        <nav>
          <ul className="nav-links">{renderNavLinks()}</ul>
        </nav>
      </header>
      
      <main className="content">
        <Outlet />
      </main>
      
      <footer className="footer">
        <p>Â© 2025 Grant Thornton Bharat LLP - All Rights Reserved.</p>
      </footer>
    </div>
  );
}

export default Layout;
