import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import axios from "axios";

const API_URL = "http://localhost:5000/api/shifts"; // Adjust this based on your backend

function Shifts({ token }) {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const fetchShifts = async () => {
      try {
        const response = await axios.get(API_URL, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const formattedEvents = response.data.map((shift) => ({
          title: `Shift: ${shift.shift_type}`,
          start: `2025-04-05T${shift.start_time}`,
          end: `2025-04-05T${shift.end_time}`,
          backgroundColor:
            shift.shift_type === "Morning"
              ? "green"
              : shift.shift_type === "Afternoon"
              ? "blue"
              : "red",
          textColor: "white",
        }));

        setEvents(formattedEvents);
      } catch (error) {
        console.error("Error fetching shifts:", error);
      }
    };

    fetchShifts();
  }, [token]);

  return (
    <div>
      <h2>Shift Management</h2>
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="timeGridDay"
        events={events}
        headerToolbar={{
          left: "prev,next today",
          center: "title",
          right: "dayGridMonth,timeGridWeek,timeGridDay",
        }}
      />
    </div>
  );
}

export default Shifts;
