import React, { useState } from "react";
import "../styles/ShiftHandover.css";

function ShiftHandover() {
  const [handoverData, setHandoverData] = useState({
    date: "",
    shift: "",
    cluster: "",
    members: "",
    actionPoints: [],
  });

  const [newAction, setNewAction] = useState({ description: "", status: "" });

  const handleAddAction = () => {
    if (newAction.description && newAction.status) {
      setHandoverData((prev) => ({
        ...prev,
        actionPoints: [...prev.actionPoints, newAction],
      }));
      setNewAction({ description: "", status: "" });
    }
  };

  const handleCellEdit = (index, field, value) => {
    const updatedActions = [...handoverData.actionPoints];
    updatedActions[index][field] = value;
    setHandoverData({ ...handoverData, actionPoints: updatedActions });
  };

  return (
    <div className="handover_wrapper">
      <h2>Shift Handover for SOC</h2>

      <div className="form_section">
        <input
          type="date"
          value={handoverData.date}
          onChange={(e) => setHandoverData({ ...handoverData, date: e.target.value })}
        />
        <input
          type="text"
          placeholder="Shift Time"
          value={handoverData.shift}
          onChange={(e) => setHandoverData({ ...handoverData, shift: e.target.value })}
        />
        <input
          type="text"
          placeholder="Cluster"
          value={handoverData.cluster}
          onChange={(e) => setHandoverData({ ...handoverData, cluster: e.target.value })}
        />
        <input
          type="text"
          placeholder="Team Members"
          value={handoverData.members}
          onChange={(e) => setHandoverData({ ...handoverData, members: e.target.value })}
        />
      </div>

      <div className="action_input">
        <textarea
          placeholder="Action Description"
          value={newAction.description}
          onChange={(e) => setNewAction({ ...newAction, description: e.target.value })}
        />
        <select
          value={newAction.status}
          onChange={(e) => setNewAction({ ...newAction, status: e.target.value })}
        >
          <option value="">Select Status</option>
          <option value="Informational">Informational</option>
          <option value="Important">Important</option>
        </select>
        <button onClick={handleAddAction}>Add Action Point</button>
      </div>

      <div className="handover_display">
        <h3>Action Points from Previous Shift</h3>
        <div className="shift_info">
          <p><strong>Date:</strong> {handoverData.date}</p>
          <p><strong>Shift:</strong> {handoverData.shift}</p>
          <p><strong>Cluster:</strong> {handoverData.cluster}</p>
          <p><strong>Team Members:</strong> {handoverData.members}</p>
        </div>

        <table className="handover_table">
          <thead>
            <tr>
              <th>S.No.</th>
              <th>Description</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {handoverData.actionPoints.map((action, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td
                  contentEditable
                  suppressContentEditableWarning
                  onBlur={(e) => handleCellEdit(index, "description", e.target.innerText)}
                >
                  {action.description}
                </td>
                <td
                  contentEditable
                  suppressContentEditableWarning
                  onBlur={(e) => handleCellEdit(index, "status", e.target.innerText)}
                >
                  {action.status}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default ShiftHandover;
