export type CustomHeaderProps<Data> = {
  children: (p: CustomDateHeaderProps<Data>) => ReactNode
  unit: keyof TimelineTimeSteps
  timeSteps: any
  visibleTimeStart: number
  visibleTimeEnd: number
  canvasTimeStart: number
  canvasTimeEnd: number
  canvasWidth: number
