import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import axios from "axios";
import { Modal, Button, Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Shifts.css";

const API_URL = "http://localhost:5000/api";

function Shifts() {
  const [shifts, setShifts] = useState([]);
  const [selectedShift, setSelectedShift] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [notes, setNotes] = useState("");
  const [editable, setEditable] = useState(false);

  useEffect(() => {
    fetchShifts();
  }, []);

  const fetchShifts = async () => {
    try {
      const response = await axios.get(`${API_URL}/shifts`);
      const formattedShifts = response.data.map((shift) => ({
        id: shift.id,
        title: `${shift.user_name} - ${shift.shift_type}`,
        start: `2025-04-05T${shift.start_time}`,
        end: `2025-04-05T${shift.end_time}`,
        backgroundColor: getShiftColor(shift.shift_type),
        textColor: "white",
        extendedProps: shift
      }));
      setShifts(formattedShifts);
    } catch (error) {
      console.error("Error fetching shifts:", error);
    }
  };

  const getShiftColor = (shiftType) => {
    switch (shiftType) {
      case "Morning":
        return "green";
      case "Afternoon":
        return "blue";
      case "Evening":
        return "red";
      default:
        return "gray";
    }
  };

  const handleEventClick = async (info) => {
    const shift = info.event.extendedProps;
    setSelectedShift(shift);
    setShowModal(true);

    try {
      const response = await axios.get(`${API_URL}/notes/${shift.id}`);
      setNotes(response.data.notes);
      
      const currentTime = new Date().toLocaleTimeString("en-GB", { hour12: false }).slice(0, 5);
      setEditable(shift.start_time <= currentTime && currentTime <= shift.end_time);
    } catch (error) {
      console.error("Error fetching notes:", error);
    }
  };

  const handleSaveNotes = async () => {
    try {
      await axios.post(`${API_URL}/notes/${selectedShift.id}`, { notes });
      setShowModal(false);
    } catch (error) {
      alert(error.response.data.error);
    }
  };

  return (
    <div className="container mt-4">
      <h2>Shift Management</h2>
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="timeGridWeek"
        events={shifts}
        eventClick={handleEventClick}
      />

      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Shift Notes</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Control as="textarea" rows={4} value={notes} onChange={(e) => setNotes(e.target.value)} disabled={!editable} />
        </Modal.Body>
        <Modal.Footer>
          {editable && <Button variant="primary" onClick={handleSaveNotes}>Save</Button>}
          <Button variant="secondary" onClick={() => setShowModal(false)}>Close</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Shifts;
