import React from "react";
import FullCalendar from "@fullcalendar/react";
import resourceTimelinePlugin from "@fullcalendar/resource-timeline";
import interactionPlugin from "@fullcalendar/interaction"; // For drag/drop/edit if needed
import dayjs from "dayjs";
import "./Shifts.css";

const Shifts = () => {
  const resources = [
    { id: "1", title: "Ryan - Cloud System Engineer" },
    { id: "2", title: "Kate - Help Desk Specialist" },
    { id: "3", title: "John - Application Developer" },
    { id: "4", title: "Mark - Network Administrator" },
    { id: "5", title: "Sharon - Data Quality Manager" },
    { id: "6", title: "Emma - Product Testing Analyst" },
  ];

  const events = [
    {
      id: "1",
      resourceId: "5",
      title: "07:00 - 13:00",
      start: "2025-04-07T07:00:00",
      end: "2025-04-07T13:00:00",
      color: "#fce96a",
    },
    {
      id: "2",
      resourceId: "6",
      title: "07:00 - 13:00",
      start: "2025-04-07T07:00:00",
      end: "2025-04-07T13:00:00",
      color: "#a8e6cf",
    },
    {
      id: "3",
      resourceId: "2",
      title: "12:00 - 18:00",
      start: "2025-04-07T12:00:00",
      end: "2025-04-07T18:00:00",
      color: "#b2f2bb",
    },
    {
      id: "4",
      resourceId: "4",
      title: "12:00 - 18:00",
      start: "2025-04-07T12:00:00",
      end: "2025-04-07T18:00:00",
      color: "#f5b5fc",
    },
    {
      id: "5",
      resourceId: "1",
      title: "07:00 - 13:00",
      start: "2025-04-08T07:00:00",
      end: "2025-04-08T13:00:00",
      color: "#fabebe",
    },
    {
      id: "6",
      resourceId: "3",
      title: "12:00 - 18:00",
      start: "2025-04-09T12:00:00",
      end: "2025-04-09T18:00:00",
      color: "#add8e6",
    },
    {
      id: "7",
      resourceId: "2",
      title: "12:00 - 18:00",
      start: "2025-04-08T12:00:00",
      end: "2025-04-08T18:00:00",
      color: "#b2f2bb",
    },
  ];

  return (
    <div className="shift-calendar-container">
      <h2 className="text-xl font-bold mb-4">Employee Shift Timeline</h2>
      <FullCalendar
        plugins={[resourceTimelinePlugin, interactionPlugin]}
        initialView="resourceTimelineDay"
        headerToolbar={{
          left: "prev,next today",
          center: "title",
          right: "resourceTimelineDay,resourceTimelineWeek",
        }}
        aspectRatio={2}
        resources={resources}
        events={events}
        resourceAreaHeaderContent="Employees"
        slotMinTime="06:00:00"
        slotMaxTime="20:00:00"
        height="auto"
      />
    </div>
  );
};

export default Shifts;
