// src/pages/Shifts.js

import React from "react";
import FullCalendar from "@fullcalendar/react";
import resourceTimelinePlugin from "@fullcalendar/resource-timeline";
import moment from "moment";

// ✅ FullCalendar v6+ CSS imports
import "@fullcalendar/common/main.css";
import "@fullcalendar/resource-timeline/main.css";

const ShiftTimeline = () => {
  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Shift Timeline</h2>
      <FullCalendar
        plugins={[resourceTimelinePlugin]}
        initialView="resourceTimelineWeek"
        headerToolbar={{
          left: "prev,next today",
          center: "title",
          right: "resourceTimelineDay,resourceTimelineWeek",
        }}
        initialDate="2025-04-07"
        slotMinTime="06:00:00"
        slotMaxTime="20:00:00"
        resourceAreaHeaderContent="Employees"
        resources={[
          { id: "1", title: "Ryan - Cloud System Engineer" },
          { id: "2", title: "Kate - Help Desk Specialist" },
          { id: "3", title: "John - Application Developer" },
          { id: "4", title: "Mark - Network Administrator" },
          { id: "5", title: "Sharon - Data Quality Manager" },
          { id: "6", title: "Emma - Product Testing Analyst" },
        ]}
        events={[
          {
            id: "1",
            resourceId: "5",
            title: "07:00 - 13:00",
            start: "2025-04-07T07:00:00",
            end: "2025-04-07T13:00:00",
            color: "#fce96a",
          },
          {
            id: "2",
            resourceId: "6",
            title: "07:00 - 13:00",
            start: "2025-04-07T07:00:00",
            end: "2025-04-07T13:00:00",
            color: "#a8e6cf",
          },
          {
            id: "3",
            resourceId: "2",
            title: "12:00 - 18:00",
            start: "2025-04-07T12:00:00",
            end: "2025-04-07T18:00:00",
            color: "#b2f2bb",
          },
          {
            id: "4",
            resourceId: "4",
            title: "12:00 - 18:00",
            start: "2025-04-07T12:00:00",
            end: "2025-04-07T18:00:00",
            color: "#f5b5fc",
          },
          {
            id: "5",
            resourceId: "1",
            title: "07:00 - 13:00",
            start: "2025-04-08T07:00:00",
            end: "2025-04-08T13:00:00",
            color: "#fabebe",
          },
          {
            id: "6",
            resourceId: "3",
            title: "12:00 - 18:00",
            start: "2025-04-09T12:00:00",
            end: "2025-04-09T18:00:00",
            color: "#add8e6",
          },
          {
            id: "7",
            resourceId: "2",
            title: "12:00 - 18:00",
            start: "2025-04-08T12:00:00",
            end: "2025-04-08T18:00:00",
            color: "#b2f2bb",
          },
        ]}
        height="auto"
      />
    </div>
  );
};

export default ShiftTimeline;
