import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import { AuthProvider, useAuth } from "../context/AuthContext";
import "../styles/Shifts.css";

const Shifts = () => {
  const { user } = useAuth(AuthProvider); // Auth context
  const [shifts, setShifts] = useState([]);

  useEffect(() => {
    const sampleShifts = [
      {
        id: 1,
        title: "Morning Shift - A",
        start: "2025-04-05T08:00:00",
        end: "2025-04-05T12:00:00",
      },
      {
        id: 2,
        title: "Afternoon Shift - B",
        start: "2025-04-05T12:00:00",
        end: "2025-04-05T16:00:00",
      },
      {
        id: 3,
        title: "Evening Shift - C",
        start: "2025-04-05T16:00:00",
        end: "2025-04-05T20:00:00",
      },
    ];
    setShifts(sampleShifts);
  }, []);

  return (
    <div className="shifts-wrapper">
      <div className="shifts-calendar">
        <FullCalendar
          plugins={[interactionPlugin, dayGridPlugin, timeGridPlugin]}
          allDaySlot={false}
          initialView="timeGridDay"
          slotMinTime={"07:30:00"}
          slotMaxTime={"20:30:00"}
          headerToolbar={{
            left: "prev,next today",
            center: "title",
            right: "timeGridDay,timeGridWeek,dayGridMonth",
          }}
          events={shifts}
          editable={false}
          height="auto"
        />
      </div>
    </div>
  );
};

export default Shifts;
